import React from "react";
import "./Header.css";
import { Link } from "react-router-dom";

function Header() {
  return (
    <nav className="header">
      {/* Logo */}
      <Link to="/ ">
        {" "}
        {/*Make logo clickable*/}
        <img
          className="header__logo"
          src="http://pngimg.com/uploads/amazon/amazon_PNG11.png"
          alt="Logo"
        />
      </Link>
      <div className="header__search">
        {/* Search*/}
        <input type="text" className="header__searchInput" />

        {/* Logo box */}
        <img src="/icons/search.svg" alt="" class="header__searchIcon" />
      </div>
      {/* Links */}
      <div className="header__nav"></div>
      {/*1st Link */}
      <Link to="/login" className="header__link">
        <div className="header__option">
          <span className="header__optionLineOne">Hello Dino</span>
          <span className="header__optionLineTwo">Sign In</span>
        </div>
      </Link>
      {/*2nd Link */}
      <Link to="/" className="header__link">
        {/*href refreshes page, link does not*/}
        <div className="header__option">
          <span className="header__optionLineOne">Returns</span>
          <span className="header__optionLineTwo">& Orders</span>
        </div>
      </Link>
      {/*3rd Link */}
      <Link to="/" className="header__link">
        <div className="header__option">
          <span className="header__optionLineOne">Your</span>
          <span className="header__optionLineTwo">Membership</span>
        </div>
      </Link>
      {/* Basket */}
      <Link to="/checkout">
        <div className="header__optionBasket">
          {/* Basket */}
          <img src="/icons/basket2.svg" alt="" class="header__searchIcon" />
          {/* Number of items */}
          <span>0</span>
        </div>
      </Link>
    </nav>
  );
}

export default Header;
